    // Generate JWT token
    const accessToken = jwt.sign({
      userId: user.id
    }, JWT_SECRET, {
      expiresIn: '12h'
    });
    res.json({
      accessToken
    });
  } catch (error) {
    console.error(error);
    res.status(500).json({
      error: 'Internal Server Error'
    });
  }
});
module.exports = router;
